import React, { useRef } from 'react';
import { Link } from 'react-router-dom';
import { motion, useScroll, useTransform } from 'framer-motion';
import { ArrowRight, Calendar, Code2, Trophy, Users } from 'lucide-react';

// You'll need to install these packages:
// npm install framer-motion lucide-react

const LandingPage = () => {
  const user = JSON.parse(localStorage.getItem('user'));
  const ref = useRef(null);
  const { scrollYProgress } = useScroll({
    target: ref,
    offset: ["start start", "end start"],
  });

  const opacity = useTransform(scrollYProgress, [0, 0.5], [1, 0]);
  const scale = useTransform(scrollYProgress, [0, 0.5], [1, 0.8]);
  const position = useTransform(scrollYProgress, (pos) => {
    return pos === 1 ? "relative" : "fixed";
  });

  const features = [
    {
      icon: <Calendar className="h-6 w-6 text-indigo-500" />,
      title: "Upcoming Hackathons",
      description: "Discover and join upcoming hackathons from around the world.",
    },
    {
      icon: <Users className="h-6 w-6 text-indigo-500" />,
      title: "Team Building",
      description: "Find teammates or join existing teams to collaborate on projects.",
    },
    {
      icon: <Code2 className="h-6 w-6 text-indigo-500" />,
      title: "Project Submissions",
      description: "Submit your projects and get feedback from judges and peers.",
    },
    {
      icon: <Trophy className="h-6 w-6 text-indigo-500" />,
      title: "Prizes & Recognition",
      description: "Win prizes and get recognized for your innovative solutions.",
    },
  ];

  return (
    <div className="flex min-h-screen flex-col bg-black text-white">
      <header className="py-4 px-6 border-b border-gray-800">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-xl font-bold">blockHUNT</h1>
          <nav>
            {user ? (
              <Link to="/hackathons" className="bg-indigo-600 hover:bg-indigo-700 px-4 py-2 rounded">
                Hackathons
              </Link>
            ) : (
              <div className="space-x-4">
                <Link to="/login" className="text-white hover:text-indigo-400">
                  Login
                </Link>
                <Link to="/signup" className="bg-indigo-600 hover:bg-indigo-700 px-4 py-2 rounded">
                  Sign Up
                </Link>
              </div>
            )}
          </nav>
        </div>
      </header>

      <main className="flex-1">
        {/* Hero Section */}
        <section ref={ref} className="relative w-full min-h-screen flex items-center justify-center overflow-hidden">
          <motion.div style={{ opacity, scale, position }} className="top-0 left-0 w-full h-screen z-10">
            <div className="absolute inset-0 bg-gradient-to-r from-indigo-900/20 to-purple-900/20"></div>
            <div className="container relative z-10 mx-auto px-4 md:px-6 py-24 md:py-32">
              <div className="grid gap-6 lg:grid-cols-[1fr_400px] lg:gap-12 xl:grid-cols-[1fr_600px]">
                <div className="flex flex-col justify-center space-y-4">
                  <div className="space-y-2">
                    <motion.div
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ duration: 0.5 }}
                    >
                      <span className="inline-block rounded-full bg-indigo-500/10 px-3 py-1 text-sm text-indigo-500 mb-4">
                        The Future of Hackathons
                      </span>
                    </motion.div>
                    <motion.h1
                      className="text-4xl font-bold tracking-tighter sm:text-5xl xl:text-7xl/none bg-clip-text text-transparent bg-gradient-to-r from-white to-gray-500"
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ duration: 0.5, delay: 0.1 }}
                    >
                      Welcome to blockHUNT
                    </motion.h1>
                    <motion.p
                      className="max-w-[600px] text-gray-400 md:text-xl mt-4"
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ duration: 0.5, delay: 0.2 }}
                    >
                      Join or organize hackathons on the blockchain! Build projects that shape the future of Web3.
                    </motion.p>
                  </div>
                  <motion.div
                    className="flex flex-col gap-2 min-[400px]:flex-row mt-4"
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.5, delay: 0.3 }}
                  >
                    {user ? (
                      <Link 
                        to="/hackathons" 
                        className="inline-flex items-center justify-center bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 text-white px-6 py-3 rounded-md font-medium"
                      >
                        Explore Hackathons <ArrowRight className="ml-2 h-4 w-4" />
                      </Link>
                    ) : (
                      <>
                        <Link 
                          to="/signup" 
                          className="inline-flex items-center justify-center bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 text-white px-6 py-3 rounded-md font-medium"
                        >
                          Sign Up <ArrowRight className="ml-2 h-4 w-4" />
                        </Link>
                        <Link 
                          to="/login" 
                          className="inline-flex items-center justify-center border border-indigo-500/20 text-white hover:bg-indigo-500/10 px-6 py-3 rounded-md font-medium"
                        >
                          Login
                        </Link>
                      </>
                    )}
                  </motion.div>
                </div>
                <motion.div
                  className="flex items-center justify-center"
                  initial={{ opacity: 0, scale: 0.8 }}
                  animate={{ opacity: 1, scale: 1 }}
                  transition={{ duration: 0.8, delay: 0.4 }}
                >
                  <div className="w-full max-w-[450px] aspect-square bg-black border border-indigo-500/20 rounded-3xl p-8 relative transform hover:translate-z-10 transition-transform duration-300">
                    <div className="relative h-full w-full">
                      <div className="absolute inset-0 flex items-center justify-center">
                        <div className="relative h-64 w-64 rounded-full bg-gradient-to-r from-indigo-500 to-purple-500 opacity-20 blur-xl" />
                      </div>

                      <div className="absolute inset-0 flex items-center justify-center">
                        <div className="relative h-48 w-48 rounded-full border border-indigo-500/30">
                          <Orbits />
                        </div>
                      </div>

                      <div className="absolute inset-0 flex items-center justify-center">
                        <div className="relative h-32 w-32 rounded-full border border-purple-500/20">
                          <Orbits speed={15} />
                        </div>
                      </div>

                      <div className="absolute inset-0 flex items-center justify-center">
                        <motion.div
                          className="h-16 w-16 rounded-full bg-gradient-to-r from-indigo-600 to-purple-600"
                          animate={{
                            boxShadow: [
                              "0 0 20px 2px rgba(79, 70, 229, 0.3)",
                              "0 0 20px 2px rgba(79, 70, 229, 0.6)",
                              "0 0 20px 2px rgba(79, 70, 229, 0.3)",
                            ],
                          }}
                          transition={{
                            duration: 2,
                            repeat: Infinity,
                            ease: "easeInOut",
                          }}
                        />
                      </div>
                    </div>
                  </div>
                </motion.div>
              </div>
            </div>
          </motion.div>
        </section>

        {/* Features Section */}
        <section id="features" className="w-full py-12 md:py-24 lg:py-32">
          <div className="container mx-auto px-4 md:px-6">
            <div className="flex flex-col items-center justify-center space-y-4 text-center">
              <div className="space-y-2">
                <div className="inline-block rounded-lg bg-indigo-500/10 px-3 py-1 text-sm text-indigo-500">
                  Features
                </div>
                <h2 className="text-3xl font-bold tracking-tighter sm:text-5xl">Everything You Need</h2>
                <p className="max-w-[900px] text-gray-500 md:text-xl/relaxed lg:text-base/relaxed xl:text-xl/relaxed dark:text-gray-400">
                  Our platform provides all the tools you need to participate in or organize blockchain hackathons.
                </p>
              </div>
            </div>
            <div className="mx-auto grid max-w-5xl grid-cols-1 gap-6 py-12 md:grid-cols-2 lg:grid-cols-2">
              {features.map((feature, index) => (
                <div key={index} className="group relative h-full overflow-hidden rounded-lg border border-indigo-500/20 bg-black/60 p-1">
                  <div className="absolute inset-0 bg-gradient-to-r from-indigo-500/10 to-purple-500/10 opacity-0 group-hover:opacity-100 transition-opacity"></div>
                  <div className="h-full rounded-lg p-5">
                    <div className="flex flex-col gap-2 p-2">
                      <div className="text-indigo-500">{feature.icon}</div>
                      <h3 className="text-xl font-bold">{feature.title}</h3>
                      <p className="text-sm text-gray-400">{feature.description}</p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </section>

        {/* CTA Section */}
        <section className="w-full py-12 md:py-24 lg:py-32 bg-gradient-to-r from-indigo-900 to-purple-900 relative overflow-hidden">
          <div className="absolute inset-0 opacity-20 bg-[radial-gradient(#444_1px,transparent_1px)] bg-[size:20px_20px]"></div>
          <div className="container relative z-10 mx-auto px-4 md:px-6">
            <div className="flex flex-col items-center justify-center space-y-4 text-center">
              <div className="space-y-2">
                <h2 className="text-3xl font-bold tracking-tighter sm:text-5xl text-white">
                  Ready to Hack?
                </h2>
                <p className="max-w-[900px] text-indigo-200 md:text-xl/relaxed lg:text-base/relaxed xl:text-xl/relaxed">
                  Join thousands of developers, designers, and innovators on our platform.
                </p>
              </div>
              <div className="flex flex-col gap-2 min-[400px]:flex-row mt-4">
                {user ? (
                  <Link to="/hackathons" className="inline-flex items-center justify-center bg-white text-indigo-600 hover:bg-gray-100 px-6 py-3 rounded-md font-medium">
                    View Hackathons
                  </Link>
                ) : (
                  <>
                    <Link to="/signup" className="inline-flex items-center justify-center bg-white text-indigo-600 hover:bg-gray-100 px-6 py-3 rounded-md font-medium">
                      Get Started
                    </Link>
                    <Link to="/login" className="inline-flex items-center justify-center text-white border border-white hover:bg-white/10 px-6 py-3 rounded-md font-medium">
                      Login
                    </Link>
                  </>
                )}
              </div>
            </div>
          </div>
        </section>
      </main>

      <footer className="py-6 px-6 border-t border-gray-800">
        <div className="container mx-auto">
          <div className="flex flex-col md:flex-row justify-between items-center">
            <div className="mb-4 md:mb-0">
              <h2 className="text-xl font-bold">blockHUNT</h2>
              <p className="text-sm text-gray-400">Join or organize hackathons on the blockchain</p>
            </div>
            <div className="text-sm text-gray-400">
              Â© {new Date().getFullYear()} blockHUNT. All rights reserved.
            </div>
          </div>
        </div>
      </footer>
    </div>
  );
};

function Orbits({ speed = 20 }) {
  return (
    <>
      <motion.div
        className="absolute left-0 top-1/2 h-2 w-2 -translate-x-1/2 -translate-y-1/2 rounded-full bg-indigo-500"
        animate={{
          x: ["0%", "100%", "0%"],
          y: ["0%", "100%", "0%"],
          rotate: [0, 360],
        }}
        transition={{
          duration: speed,
          repeat: Infinity,
          ease: "linear",
        }}
      />

      <motion.div
        className="absolute right-0 top-1/2 h-3 w-3 translate-x-1/2 -translate-y-1/2 rounded-full bg-purple-500"
        animate={{
          x: ["0%", "-100%", "0%"],
          y: ["0%", "100%", "0%"],
          rotate: [0, -360],
        }}
        transition={{
          duration: speed * 1.2,
          repeat: Infinity,
          ease: "linear",
        }}
      />

      <motion.div
        className="absolute bottom-0 left-1/2 h-2 w-2 -translate-x-1/2 translate-y-1/2 rounded-full bg-pink-500"
        animate={{
          x: ["0%", "100%", "0%"],
          y: ["0%", "-100%", "0%"],
          rotate: [0, 360],
        }}
        transition={{
          duration: speed * 0.8,
          repeat: Infinity,
          ease: "linear",
        }}
      />
    </>
  );
}

export default LandingPage;
