import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import SubmissionTable from '../components/SubmissionTable';
import { toast } from 'react-toastify';

const SubmissionsPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [submissions, setSubmissions] = useState([]);
  const [hackathon, setHackathon] = useState(null);
  const [loading, setLoading] = useState(false);
  const [step, setStep] = useState(1);

  useEffect(() => {
    fetchData();
  }, [id, navigate]);

  const fetchData = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      const hackathonResponse = await axios.get(`http://localhost:3000/api/hackathons/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log('Fetched hackathon:', hackathonResponse.data);
      setHackathon(hackathonResponse.data);

      if (hackathonResponse.data.prizes_distributed) {
        setStep(4);
      } else if (hackathonResponse.data.winners && JSON.parse(hackathonResponse.data.winners).length > 0) {
        setStep(3);
      } else if (hackathonResponse.data.funded_amount > 0) {
        setStep(2);
      } else if (hackathonResponse.data.manually_ended) {
        setStep(1);
      }

      const submissionsResponse = await axios.get(`http://localhost:3000/api/hackathons/${id}/submissions`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setSubmissions(submissionsResponse.data);
    } catch (error) {
      console.error('Fetch data error:', error);
      if (error.response?.status === 403) {
        toast.error('Session expired. Please log in again.');
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        toast.error('Failed to fetch data');
        navigate('/hackathons');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleRefresh = async () => {
    await fetchData(); // Re-fetch data to update state
  };

  const handleDistributePrizes = async () => {
    if (!hackathon.winners || JSON.parse(hackathon.winners).length === 0) {
      toast.error('Please select winners first');
      return;
    }

    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `http://localhost:3000/api/hackathons/${id}/distribute`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      toast.success(`Prizes distributed! Transaction Hash: ${response.data.transactionHash}`);
      await fetchData(); // Update state after distribution
      setStep(4);
    } catch (error) {
      console.error('Error distributing prizes:', error);
      toast.error(error.response?.data?.error || 'Failed to distribute prizes');
    } finally {
      setLoading(false);
    }
  };

  const goToHackathonDetails = () => {
    navigate(`/hackathons/${id}`);
  };

  if (loading && !hackathon) {
    return (
      <div className="text-center mt-5">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (!hackathon) {
    return <p>Loading...</p>;
  }

  return (
    <div className="container mt-5">
      <h2>Submissions for {hackathon.title}</h2>

      <div className="alert alert-info mb-4">
        <h4>Hackathon Prize Distribution Workflow</h4>
        <ol className="mb-0">
          <li className={hackathon.manually_ended ? "text-success fw-bold" : ""}>
            End Hackathon {hackathon.manually_ended && "✓"}
          </li>
          <li className={hackathon.funded_amount > 0 ? "text-success fw-bold" : ""}>
            Fund Hackathon {hackathon.funded_amount > 0 && "✓"}
            {hackathon.manually_ended && !hackathon.funded_amount && !hackathon.prizes_distributed && (
              <button 
                className="btn btn-sm btn-primary ms-2" 
                onClick={goToHackathonDetails}
              >
                Go to Details
              </button>
            )}
          </li>
          <li className={hackathon.winners && JSON.parse(hackathon.winners).length > 0 ? "text-success fw-bold" : ""}>
            Select Winners & Distribute {hackathon.prizes_distributed && "✓"}
          </li>
        </ol>
      </div>

      {step === 1 && !hackathon.manually_ended && (
        <div className="alert alert-warning">
          First, end the hackathon below.
        </div>
      )}
      {step === 1 && hackathon.manually_ended && !hackathon.funded_amount && (
        <div className="alert alert-warning">
          The hackathon is ended. Next, fund the hackathon below.
        </div>
      )}
      {step === 2 && (
        <div className="alert alert-warning">
          The hackathon is funded. Now select 1st, 2nd, and 3rd place winners and distribute prizes below.
        </div>
      )}
      {step === 3 && (
        <div className="alert alert-success">
          Prizes have been distributed! The hackathon is complete.
        </div>
      )}

      <SubmissionTable
        submissions={submissions}
        hackathon={hackathon}
        onSetWinners={handleRefresh} // Renamed to onRefresh for clarity
        onDistributePrizes={handleDistributePrizes}
      />
    </div>
  );
};

export default SubmissionsPage;
