import React, { useState } from 'react';
import { toast } from 'react-toastify';
import axios from 'axios';
import { ethers } from 'ethers';

const SubmissionTable = ({ submissions, hackathon, onSetWinners, onDistributePrizes }) => {
  const [selectedWinners, setSelectedWinners] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Handle checkbox selection
  const handleWinnerSelection = (publicKey) => {
    if (selectedWinners.includes(publicKey)) {
      // If already selected, remove from array
      setSelectedWinners(selectedWinners.filter(key => key !== publicKey));
    } else {
      // If not selected and we have less than 3 winners, add to array
      if (selectedWinners.length < 3) {
        setSelectedWinners([...selectedWinners, publicKey]);
      } else {
        toast.warning('You can select at most 3 winners');
      }
    }
  };

  // Determine if a submission is already a winner
  const isWinner = (publicKey) => {
    if (hackathon.winners && Array.isArray(JSON.parse(hackathon.winners))) {
      return JSON.parse(hackathon.winners).some(winner => winner.public_key === publicKey);
    }
    return false;
  };

  // Handle setting winners
  const handleSetWinners = async () => {
    if (selectedWinners.length === 0) {
      toast.error('Please select at least one winner');
      return;
    }

    // Validate Ethereum addresses
    for (const address of selectedWinners) {
      if (!ethers.isAddress(address)) {
        toast.error(`Invalid Ethereum address: ${address}`);
        return;
      }
    }

    setIsSubmitting(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `http://localhost:3000/api/hackathons/${hackathon.id}/set-winners`,
        { winners: selectedWinners },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      toast.success('Winners set successfully!');

      // Call the parent component's callback to update hackathon data
      onSetWinners();

      // Clear selection
      setSelectedWinners([]);
    } catch (error) {
      console.error('Error setting winners:', error);
      toast.error(error.response?.data?.error || 'Failed to set winners');
    } finally {
      setIsSubmitting(false);
    }
  };

  // Render no submissions message if there are none
  if (!submissions || submissions.length === 0) {
    return <p>No submissions yet for this hackathon.</p>;
  }

  // Check if winners are already selected
  const hasWinners = hackathon.winners && 
                    Array.isArray(JSON.parse(hackathon.winners)) && 
                    JSON.parse(hackathon.winners).length > 0;

  // Check if prizes are already distributed
  const prizesDistributed = hackathon.prizes_distributed || false;

  return (
    <div className="submission-table-container">
      <div className="table-responsive">
        <table className="table table-bordered table-striped">
          <thead className="table-dark">
            <tr>
              <th>Project Name</th>
              <th>Description</th>
              <th>GitHub URL</th>
              <th>Participant</th>
              <th>Public Key</th>
              <th>Submission Date</th>
              {!hasWinners && !prizesDistributed && (
                <th>Select as Winner</th>
              )}
              {hasWinners && (
                <th>Winner Status</th>
              )}
            </tr>
          </thead>
          <tbody>
            {submissions.map((submission) => (
              <tr key={submission.id}>
                <td>{submission.project_name}</td>
                <td>{submission.description}</td>
                <td>
                  <a href={submission.github_url} target="_blank" rel="noopener noreferrer">
                    {submission.github_url}
                  </a>
                </td>
                <td>{submission.email}</td>
                <td>
                  <small className="text-muted">
                    {submission.public_key.slice(0, 10)}...{submission.public_key.slice(-8)}
                  </small>
                </td>
                <td>{new Date(submission.created_at).toLocaleString()}</td>
                {!hasWinners && !prizesDistributed && (
                  <td>
                    <div className="form-check">
                      <input
                        type="checkbox"
                        className="form-check-input"
                        id={`winner-${submission.id}`}
                        checked={selectedWinners.includes(submission.public_key)}
                        onChange={() => handleWinnerSelection(submission.public_key)}
                        disabled={prizesDistributed || isSubmitting}
                      />
                      <label className="form-check-label" htmlFor={`winner-${submission.id}`}>
                        Select
                      </label>
                    </div>
                  </td>
                )}
                {hasWinners && (
                  <td>
                    {isWinner(submission.public_key) ? (
                      <span className="badge bg-success">Winner</span>
                    ) : (
                      <span className="badge bg-secondary">Not Selected</span>
                    )}
                  </td>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Winner selection controls */}
      {!hasWinners && !prizesDistributed && selectedWinners.length > 0 && (
        <div className="mt-3">
          <div className="alert alert-info">
            <strong>Selected Winners ({selectedWinners.length}/3):</strong>
            <ol>
              {selectedWinners.map((winner, index) => (
                <li key={winner}>
                  {winner.slice(0, 10)}...{winner.slice(-8)}
                  {index === 0 && " (1st Place - 50% or 70% or 100%)"}
                  {index === 1 && " (2nd Place - 30%)"}
                  {index === 2 && " (3rd Place - 20%)"}
                </li>
              ))}
            </ol>
            <div className="alert alert-warning mt-2">
              <strong>Prize Distribution:</strong>
              <ul className="mb-0">
                {selectedWinners.length === 1 && <li>Single winner receives 100% of the prize pool</li>}
                {selectedWinners.length === 2 && (
                  <>
                    <li>1st place: 70% of prize pool</li>
                    <li>2nd place: 30% of prize pool</li>
                  </>
                )}
                {selectedWinners.length === 3 && (
                  <>
                    <li>1st place: 50% of prize pool</li>
                    <li>2nd place: 30% of prize pool</li>
                    <li>3rd place: 20% of prize pool</li>
                  </>
                )}
              </ul>
            </div>
          </div>
          <button 
            className="btn btn-primary me-2" 
            onClick={handleSetWinners} 
            disabled={isSubmitting}
          >
            {isSubmitting ? 'Processing...' : 'Set Winners'}
          </button>
        </div>
      )}

      {/* Prize distribution button - shown after winners are set but before prizes are distributed */}
      {hackathon.winners && 
       JSON.parse(hackathon.winners).length > 0 && 
       !hackathon.prizes_distributed && (
        <div className="mt-3">
          <div className="alert alert-info">
            <strong>Winners:</strong>
            <ol>
              {JSON.parse(hackathon.winners).map((winner, index) => {
                const address = winner.public_key;
                let percentage = "100%";
                if (JSON.parse(hackathon.winners).length === 2) {
                  percentage = index === 0 ? "70%" : "30%";
                } else if (JSON.parse(hackathon.winners).length === 3) {
                  percentage = index === 0 ? "50%" : (index === 1 ? "30%" : "20%");
                }

                return (
                  <li key={address}>
                    {address.slice(0, 10)}...{address.slice(-8)} ({percentage})
                  </li>
                );
              })}
            </ol>
          </div>
          <button 
            className="btn btn-success" 
            onClick={onDistributePrizes} 
            disabled={isSubmitting}
          >
            {isSubmitting ? 'Processing...' : 'Distribute Prizes'}
          </button>
        </div>
      )}

      {/* Show distribution results if prizes are distributed */}
      {hackathon.prizes_distributed && (
        <div className="alert alert-success mt-3">
          <strong>Prizes have been distributed!</strong>
          <p>The prize fund of {hackathon.funded_amount} ETH has been distributed to the winners.</p>
          <ol>
            {JSON.parse(hackathon.winners).map((winner, index) => {
              const address = winner.public_key;
              let percentage = "100%";
              let amount = hackathon.funded_amount;

              if (JSON.parse(hackathon.winners).length === 2) {
                percentage = index === 0 ? "70%" : "30%";
                amount = index === 0 
                  ? (hackathon.funded_amount * 0.7).toFixed(4) 
                  : (hackathon.funded_amount * 0.3).toFixed(4);
              } else if (JSON.parse(hackathon.winners).length === 3) {
                if (index === 0) {
                  percentage = "50%";
                  amount = (hackathon.funded_amount * 0.5).toFixed(4);
                } else if (index === 1) {
                  percentage = "30%";
                  amount = (hackathon.funded_amount * 0.3).toFixed(4);
                } else {
                  percentage = "20%";
                  amount = (hackathon.funded_amount * 0.2).toFixed(4);
                }
              }

              return (
                <li key={address}>
                  {address.slice(0, 10)}...{address.slice(-8)}: {amount} ETH ({percentage})
                </li>
              );
            })}
          </ol>
          <p className="mb-0">
            <strong>Distributed at:</strong> {new Date(hackathon.prizes_distributed_at).toLocaleString()}
          </p>
        </div>
      )}
    </div>
  );
};

export default SubmissionTable;
