import React from 'react';
import { Link } from 'react-router-dom';

const LandingPage = () => {
  const user = JSON.parse(localStorage.getItem('user'));

  return (
    <div className="container text-center mt-5">
      <h1>Welcome to blockHUNT</h1>
      <p className="lead">Join or organize hackathons on the blockchain!</p>
      {user ? (
        <p>
          Welcome, {user.email}! Check out the{' '}
          <Link to="/hackathons" className="btn btn-primary">Hackathons</Link>.
        </p>
      ) : (
        <p>
          <Link to="/signup" className="btn btn-primary me-2">Sign Up</Link>
          <Link to="/login" className="btn btn-secondary">Login</Link>
        </p>
      )}
    </div>
  );
};

export default LandingPage;
