import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SubmissionTable from './SubmissionTable';

const HackathonDetailsPage = ({ hackathonId }) => {
  const [hackathon, setHackathon] = useState(null);
  const [submissions, setSubmissions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchHackathonData = async () => {
    setLoading(true);
    setError(null);
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Authentication token not found. Please log in.');
      }

      const hackathonResponse = await axios.get(
        `http://localhost:3000/api/hackathons/${hackathonId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setHackathon(hackathonResponse.data);

      const submissionsResponse = await axios.get(
        `http://localhost:3000/api/hackathons/${hackathonId}/submissions`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setSubmissions(submissionsResponse.data);
    } catch (error) {
      console.error('Error fetching hackathon data:', error);
      if (error.message === 'Authentication token not found. Please log in.' || error.response?.status === 401) {
        localStorage.removeItem('token');
        window.location.href = '/login';
      } else {
        setError(error.response?.data?.error || error.message || 'Failed to load hackathon data.');
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchHackathonData();
  }, [hackathonId]);

  const handleSetWinners = (data) => {
    if (data) {
      if (data.hackathon) {
        setHackathon(data.hackathon);
      }
    } else {
      fetchHackathonData();
    }
  };

  // Truncate transaction hash for display
  const truncateHash = (hash) => {
    if (!hash) return '';
    return `${hash.slice(0, 6)}...${hash.slice(-4)}`;
  };

  // Extract distributions from hackathon.winners
  const distributions = hackathon?.winners && hackathon.prizes_distributed
    ? (Array.isArray(hackathon.winners) ? hackathon.winners : JSON.parse(hackathon.winners))
        .filter(w => w.amount && w.transaction_hash)
        .map(w => ({
          winner: w.public_key,
          amount: w.amount,
          transactionHash: w.transaction_hash,
          distributedAt: hackathon.prizes_distributed_at
        }))
    : [];

  if (loading) {
    return (
      <div className="text-center py-10">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
        <p className="mt-2">Loading hackathon details...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger text-center" role="alert">
        <h4 className="alert-heading">Error</h4>
        <p>{error}</p>
        <button className="btn btn-primary" onClick={fetchHackathonData}>
          Retry
        </button>
      </div>
    );
  }

  if (!hackathon) {
    return (
      <div className="alert alert-warning text-center" role="alert">
        <h4 className="alert-heading">Hackathon Not Found</h4>
        <p>No hackathon data available.</p>
      </div>
    );
  }

  return (
    <div className="container py-6">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-3xl font-bold">{hackathon.title}</h2>
        <button
          className="btn btn-secondary"
          onClick={fetchHackathonData}
          disabled={loading}
        >
          {loading ? 'Refreshing...' : 'Refresh'}
        </button>
      </div>
      <div className="card bg-gray-900 text-white p-6 rounded-lg shadow-lg">
        <h3 className="text-xl font-semibold mb-2">Details</h3>
        <p className="text-gray-300 mb-2">{hackathon.description}</p>
        <p className="text-gray-400">
          <strong>Start Date:</strong> {new Date(hackathon.start_date).toLocaleDateString()}
        </p>
        <p className="text-gray-400">
          <strong>End Date:</strong> {new Date(hackathon.end_date).toLocaleDateString()}
        </p>
        <p className="text-gray-400">
          <strong>Prize Pool:</strong> {hackathon.prize_pool} ETH
        </p>
        <p className="text-gray-400">
          <strong>Participants:</strong> {hackathon.participant_count || 0}
        </p>
        <p className="text-gray-400">
          <strong>Submissions:</strong> {hackathon.submission_count || 0}
        </p>
        <p className="text-gray-400">
          <strong>Status:</strong>{' '}
          {hackathon.status === 'ended' || hackathon.manually_ended ? 'Ended' : 'Active'}
        </p>
        {hackathon.funded_amount > 0 && (
          <p className="text-gray-400">
            <strong>Funded Amount:</strong> {hackathon.funded_amount} ETH
          </p>
        )}
      </div>
      {distributions.length > 0 && (
        <div className="mt-6">
          <h3 className="text-2xl font-semibold mb-4">Prize Distribution</h3>
          <div className="card bg-gray-900 text-white p-6 rounded-lg shadow-lg">
            <ul className="list-group">
              {distributions.map((dist, index) => (
                <li key={index} className="list-group-item bg-gray-800 text-gray-300">
                  <strong>Winner {index + 1}:</strong> {dist.winner} - {dist.amount} ETH
                  <br />
                  <small>
                    Transaction:{' '}
                    <a
                      href={`https://sepolia.etherscan.io/tx/${dist.transactionHash}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-blue-400 hover:underline"
                    >
                      {truncateHash(dist.transactionHash)}
                    </a>
                  </small>
                  <br />
                  <small>Distributed at: {new Date(dist.distributedAt).toLocaleString()}</small>
                </li>
              ))}
            </ul>
          </div>
        </div>
      )}
      <div className="mt-6">
        <h3 className="text-2xl font-semibold mb-4">Submissions</h3>
        <SubmissionTable
          submissions={submissions}
          hackathon={hackathon}
          onSetWinners={handleSetWinners}
        />
      </div>
    </div>
  );
};

export default HackathonDetailsPage;
