import React, { useState, useEffect } from 'react';
import HackathonCard from '../components/HackathonCard';
import axios from 'axios';
import { toast } from 'react-toastify';

const HackathonsPage = () => {
  const [hackathons, setHackathons] = useState([]);
  const [loading, setLoading] = useState(false);
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchHackathons = async () => {
      setLoading(true);
      try {
        const response = await axios.get('http://localhost:3000/api/hackathons');
        setHackathons(response.data);
      } catch (err) {
        console.error('Fetch hackathons error:', err);
        toast.error('Failed to load hackathons');
      } finally {
        setLoading(false);
      }
    };
    fetchHackathons();
  }, []);

  if (loading) {
    return (
      <div className="text-center mt-6">
        <div className="spinner-border" role="status">
          <span className="visually-hidden"></span>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-6">
      <h2 className="text-2xl font-semibold mb-4">Active Hackathons</h2>
      {hackathons.length === 0 ? (
        <p>No active hackathons available.</p>
      ) : (
        hackathons.map((hackathon) => (
          <HackathonCard
            key={hackathon.id}
            hackathon={hackathon}
            user={user}
            isOrganizer={user?.role === 'organizer' && user?.id === hackathon.organizer_id}
            onFund={(id) => {
              setHackathons(hackathons.map(h =>
                h.id === id ? { ...h, funded_amount: h.prize_pool, funded_at: new Date().toISOString() } : h
              ));
            }}
            onEnd={(id) => {
              setHackathons(hackathons.map(h =>
                h.id === id ? { ...h, status: 'ended', manually_ended: true, manually_ended_at: new Date().toISOString() } : h
              ));
            }}
          />
        ))
      )}
    </div>
  );
};

export default HackathonsPage;
