import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import { motion } from 'framer-motion';
import { Mail, Lock, User } from 'lucide-react';

const SignupPage = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ email: '', password: '', role: 'user' });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  const validateForm = () => {
    const newErrors = {};
    if (!formData.email) newErrors.email = 'Email is required';
    else if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = 'Email is invalid';
    if (!formData.password) newErrors.password = 'Password is required';
    else if (formData.password.length < 6) newErrors.password = 'Password must be at least 6 characters';
    if (!['user', 'organizer'].includes(formData.role)) newErrors.role = 'Invalid role';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:3000/api/signup', formData, { timeout: 5000 });
      toast.success(response.data.message);
      navigate('/login');
    } catch (err) {
      const errorMessage = err.code === 'ECONNABORTED' ? 'Request timed out.' : err.response?.data?.error || 'Signup failed';
      toast.error(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-black text-white flex items-center justify-center py-12">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="bg-gray-900/50 border border-indigo-500/20 rounded-xl p-8 w-full max-w-md shadow-lg"
      >
        <h2 className="text-3xl font-bold text-center bg-gradient-to-r from-indigo-400 to-purple-400 bg-clip-text text-transparent mb-6">
          Sign Up
        </h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-6">
            <label className="flex items-center text-gray-300 mb-2"><Mail className="h-5 w-5 mr-2" /> Email</label>
            <input
              type="email"
              name="email"
              className={`w-full bg-gray-800 border ${errors.email ? 'border-red-500' : 'border-indigo-500/30'} rounded-lg p-3 text-gray-200 focus:outline-none focus:border-indigo-500 transition-all`}
              value={formData.email}
              onChange={handleChange}
            />
            {errors.email && <p className="text-red-400 text-sm mt-1">{errors.email}</p>}
          </div>
          <div className="mb-6">
            <label className="flex items-center text-gray-300 mb-2"><Lock className="h-5 w-5 mr-2" /> Password</label>
            <input
              type="password"
              name="password"
              className={`w-full bg-gray-800 border ${errors.password ? 'border-red-500' : 'border-indigo-500/30'} rounded-lg p-3 text-gray-200 focus:outline-none focus:border-indigo-500 transition-all`}
              value={formData.password}
              onChange={handleChange}
            />
            {errors.password && <p className="text-red-400 text-sm mt-1">{errors.password}</p>}
          </div>
          <div className="mb-6">
            <label className="flex items-center text-gray-300 mb-2"><User className="h-5 w-5 mr-2" /> Role</label>
            <select
              name="role"
              className={`w-full bg-gray-800 border ${errors.role ? 'border-red-500' : 'border-indigo-500/30'} rounded-lg p-3 text-gray-200 focus:outline-none focus:border-indigo-500 transition-all`}
              value={formData.role}
              onChange={handleChange}
            >
              <option value="user">User</option>
              <option value="organizer">Organizer</option>
            </select>
            {errors.role && <p className="text-red-400 text-sm mt-1">{errors.role}</p>}
          </div>
          <button
            type="submit"
            className="w-full bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 text-white py-3 rounded-full font-semibold flex items-center justify-center shadow-lg transition-all disabled:opacity-50"
            disabled={loading}
          >
            {loading ? <span className="animate-spin h-5 w-5 border-2 border-t-transparent rounded-full"></span> : 'Sign Up'}
          </button>
        </form>
        <p className="text-gray-400 text-center mt-4">
          Already have an account? <Link to="/login" className="text-indigo-400 hover:text-indigo-300">Login</Link>
        </p>
      </motion.div>
    </div>
  );
};

export default SignupPage;
