import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import HackathonCard from '../components/HackathonCard';
import { toast } from 'react-toastify';

const HackathonDetailsPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [hackathon, setHackathon] = useState(null);
  const [participants, setParticipants] = useState([]);
  const [loading, setLoading] = useState(false);
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const isOrganizer = user && user.role === 'organizer' && hackathon && user.id === hackathon.organizer_id;

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        const hackathonResponse = await axios.get(`http://localhost:3000/api/hackathons/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        console.log('Hackathon data loaded:', hackathonResponse.data);
        setHackathon(hackathonResponse.data);

        const participantsResponse = await axios.get(`http://localhost:3000/api/hackathons/${id}/participants`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setParticipants(participantsResponse.data);
      } catch (err) {
        console.error('Fetch data error:', err);
        toast.error('Failed to load hackathon details');
        navigate('/hackathons');
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [id, navigate]);

  const handleJoin = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        `http://localhost:3000/api/hackathons/${id}/join`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      toast.success('Joined hackathon successfully!');
      setParticipants([...participants, { id: user.id, email: user.email }]);
    } catch (error) {
      console.error('Join error:', error);
      toast.error('Failed to join hackathon: ' + (error.response?.data?.error || 'Unknown error'));
    } finally {
      setLoading(false);
    }
  };

  const handleWithdraw = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        `http://localhost:3000/api/hackathons/${id}/withdraw`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      toast.success('Withdrawn from hackathon successfully!');
      setParticipants(participants.filter(p => p.id !== user.id));
    } catch (error) {
      console.error('Withdraw error:', error);
      toast.error('Failed to withdraw: ' + (error.response?.data?.error || 'Unknown error'));
    } finally {
      setLoading(false);
    }
  };

  const handleEndHackathon = async () => {
    console.log('Ending hackathon:', id);
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Authentication token missing');
      }

      let response;
      try {
        response = await axios.post(
          `http://localhost:3000/api/hackathons/${id}/end-only`,
          {},
          { headers: { Authorization: `Bearer ${token}` } }
        );
      } catch (err) {
        console.log('First endpoint failed, trying alternative');
        response = await axios.post(
          `http://localhost:3000/api/hackathons/${id}/end`,
          {},
          { headers: { Authorization: `Bearer ${token}` } }
        );
      }

      console.log('End hackathon response:', response.data);
      toast.success('Hackathon ended successfully!');

      setHackathon(prev => ({
        ...prev,
        status: 'ended',
        manually_ended: true,
        manually_ended_at: new Date().toISOString()
      }));

      setTimeout(() => {
        window.location.reload();
      }, 1500);
    } catch (error) {
      console.error('End hackathon error:', error);
      const errorMsg = error.response?.data?.error || error.message || 'Unknown error';
      toast.error('Failed to end hackathon: ' + errorMsg);
    } finally {
      setLoading(false);
    }
  };

  const handleFundHackathon = async () => {
    console.log('Funding hackathon:', id);
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Authentication token missing');
      }

      const response = await axios.post(
        `http://localhost:3000/api/hackathons/${id}/fund`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      console.log('Fund hackathon response:', response.data);
      toast.success('Hackathon funded successfully!');

      setHackathon(prev => ({
        ...prev,
        funded_amount: prev.prize_pool,
        funded_at: new Date().toISOString()
      }));

      setTimeout(() => {
        window.location.reload();
      }, 1500);
    } catch (error) {
      console.error('Fund hackathon error:', error);
      const errorMsg = error.response?.data?.error || error.message || 'Unknown error';
      toast.error('Failed to fund hackathon: ' + errorMsg);
    } finally {
      setLoading(false);
    }
  };

  if (loading && !hackathon) {
    return (
      <div className="text-center mt-6">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (!hackathon) {
    return null;
  }

  const isParticipant = participants.some(p => p.id === user?.id);
  const canSubmit = user && !isOrganizer && isParticipant && hackathon.status === 'active';
  const canFund = isOrganizer && hackathon.status === 'ended' && !hackathon.funded_amount;

  return (
    <div className="container mt-6">
      <h2 className="text-3xl font-bold bg-gradient-to-r from-indigo-400 to-purple-400 bg-clip-text text-transparent mb-6">
        Hackathon Details
      </h2>
      <HackathonCard
        hackathon={hackathon}
        user={user}
        isOrganizer={isOrganizer}
        onFund={handleFundHackathon}
        onEnd={handleEndHackathon}
      />

      {isOrganizer && (
        <div className="mb-4 mt-4">
          <h4 className="text-xl font-semibold bg-gradient-to-r from-indigo-400 to-purple-400 bg-clip-text text-transparent mb-3">
            Organizer Actions
          </h4>
          <div className="flex flex-wrap gap-3">
            {hackathon.status === 'active' && (
              <button 
                className="btn btn-danger me-2 hover:shadow-lg hover:scale-105 transition-all" 
                onClick={handleEndHackathon}
                disabled={loading}
              >
                End Hackathon
              </button>
            )}
            {canFund && (
              <button 
                className="btn btn-success me-2 hover:shadow-lg hover:scale-105 transition-all" 
                onClick={handleFundHackathon}
                disabled={loading}
              >
                Fund Hackathon
              </button>
            )}
            <Link to={`/hackathons/${id}/submissions`} className="btn btn-primary hover:shadow-lg hover:scale-105 transition-all">
              View Submissions
            </Link>
          </div>
        </div>
      )}

      <h3 className="mt-4 text-2xl font-semibold bg-gradient-to-r from-indigo-400 to-purple-400 bg-clip-text text-transparent">
        Participants ({participants.length})
      </h3>
      {participants.length === 0 ? (
        <p className="text-gray-400">No participants yet.</p>
      ) : (
        <ul className="list-group w-50 mx-auto">
          {participants.map((participant) => (
            <li key={participant.id} className="list-group-item">{participant.email}</li>
          ))}
        </ul>
      )}

      {user && !isOrganizer && hackathon.status === 'active' && (
        <div className="mt-4 flex flex-wrap gap-3">
          {isParticipant ? (
            <>
              <button
                className="btn btn-danger me-2 hover:shadow-lg hover:scale-105 transition-all"
                onClick={handleWithdraw}
                disabled={loading}
              >
                Withdraw
              </button>
              {canSubmit && (
                <Link to={`/hackathons/${id}/submit`} className="btn btn-primary hover:shadow-lg hover:scale-105 transition-all">
                  Submit Project
                </Link>
              )}
            </>
          ) : (
            <button
              className="btn btn-success hover:shadow-lg hover:scale-105 transition-all"
              onClick={handleJoin}
              disabled={loading}
            >
              Join Hackathon
            </button>
          )}
        </div>
      )}
    </div>
  );
};

export default HackathonDetailsPage;
