import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { ethers } from 'ethers';
import { Modal, Button } from 'react-bootstrap';
import { toast } from 'react-toastify';

const SubmitProjectPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user')) || {};
  const [hackathon, setHackathon] = useState(null);
  const [existingSubmission, setExistingSubmission] = useState(null);
  const [formData, setFormData] = useState({
    idea: '',
    description: '',
    public_key: user?.wallet_address || '',
    number_of_teammates: 0,
    teammate_names: '',
    github_link: '',
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          toast.error('Please log in to continue');
          navigate('/login');
          return;
        }

        const hackathonResponse = await axios.get(`http://localhost:3000/api/hackathons/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
          timeout: 5000,
        });
        setHackathon(hackathonResponse.data);

        try {
          const submissionResponse = await axios.get(`http://localhost:3000/api/hackathons/${id}/submission`, {
            headers: { Authorization: `Bearer ${token}` },
            timeout: 5000,
          });
          if (submissionResponse.status === 200) {
            const submission = submissionResponse.data;
            setExistingSubmission(submission);
            setFormData({
              idea: submission.idea,
              description: submission.description || '',
              public_key: submission.public_key,
              number_of_teammates: submission.number_of_teammates || 0,
              teammate_names: submission.teammate_names || '',
              github_link: submission.github_link || '',
            });
          }
        } catch (err) {
          if (err.response?.status !== 404) {
            throw err;
          }
        }
      } catch (err) {
        console.error('Fetch data error:', err);
        let errorMessage = 'Failed to load hackathon details';
        if (err.code === 'ECONNABORTED') {
          errorMessage = 'Request timed out. Please check if the server is running.';
        } else if (err.response) {
          errorMessage = err.response.data?.error || 'Failed to load hackathon details';
        }
        setErrors({ fetch: errorMessage });
        toast.error(errorMessage);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id, navigate]);

  const validateForm = () => {
    const newErrors = {};
    if (!formData.idea) newErrors.idea = 'Idea is required';
    if (!formData.description) newErrors.description = 'Description is required';
    if (!formData.public_key) newErrors.public_key = 'Wallet address is required';
    if (formData.number_of_teammates < 0) newErrors.number_of_teammates = 'Number of teammates cannot be negative';
    if (formData.github_link && !/^https?:\/\/(www\.)?github\.com\/.+$/.test(formData.github_link)) {
      newErrors.github_link = 'Please enter a valid GitHub URL';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleConnectWallet = async () => {
    try {
      if (!window.ethereum) {
        setErrors({ wallet: 'Please install MetaMask to connect your wallet' });
        toast.error('Please install MetaMask to connect your wallet');
        return;
      }

      const provider = new ethers.BrowserProvider(window.ethereum);
      await provider.send('eth_requestAccounts', []);
      const signer = await provider.getSigner();
      const address = await signer.getAddress();

      const token = localStorage.getItem('token');
      await axios.post(
        'http://localhost:3000/api/connect-wallet',
        { wallet_address: address },
        { headers: { Authorization: `Bearer ${token}` }, timeout: 5000 }
      );

      setFormData({ ...formData, public_key: address });
      localStorage.setItem('user', JSON.stringify({ ...user, wallet_address: address }));
      toast.success('Wallet connected successfully!');
    } catch (err) {
      console.error('Connect wallet error:', err);
      let errorMessage = 'Failed to connect wallet';
      if (err.code === 'ECONNABORTED') {
        errorMessage = 'Request timed out. Please check if the server is running.';
      } else if (err.response) {
        errorMessage = err.response.data?.error || 'Failed to connect wallet';
      }
      setErrors({ wallet: errorMessage });
      toast.error(errorMessage);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    setShowModal(true);
  };

  const confirmSubmit = async () => {
    if (submitting) return;
    setSubmitting(true);
    setShowModal(false);
    setLoading(true);
    try {
      console.log('Submitting project:', {
        url: existingSubmission
          ? `http://localhost:3000/api/hackathons/${id}/submission`
          : `http://localhost:3000/api/hackathons/${id}/submit`,
        data: formData,
      });
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No authentication token found. Please log in again.');
      }

      const url = existingSubmission
        ? `http://localhost:3000/api/hackathons/${id}/submission`
        : `http://localhost:3000/api/hackathons/${id}/submit`;
      const method = existingSubmission ? 'put' : 'post';

      const cleanFormData = {
        idea: formData.idea.trim(),
        public_key: formData.public_key.trim(),
        teammate_names: formData.teammate_names.trim(),
        github_link: formData.github_link.trim(),
        description: formData.description.trim(),
        number_of_teammates: formData.number_of_teammates, // Add this line if neededS
      };

      const response = await axios({
        method,
        url,
        data: cleanFormData,
        headers: { Authorization: `Bearer ${token}` },
        timeout: 5000,
      });

      console.log('Submission successful:', response.data);
      toast.success(response.data.message);
      navigate(`/hackathons`);
    } catch (err) {
      console.error('Submit project error:', err);
      let errorMessage = 'Failed to submit project';
      if (err.code === 'ECONNABORTED') {
        errorMessage = 'Request timed out. Please check if the server is running.';
      } else if (err.response) {
        errorMessage = err.response.data?.error || 'Failed to submit project';
      } else if (err.request) {
        errorMessage = 'No response from server. Please check your network connection or server status.';
      } else {
        errorMessage = err.message || 'An unexpected error occurred';
      }
      console.log('Error details:', err.response || err.request || err.message);
      setErrors({ submit: errorMessage });
      toast.error(errorMessage);
    } finally {
      setLoading(false);
      setSubmitting(false);
      console.log('Loading and submitting states reset');
    }
  };

  if (loading && !hackathon) {
    return (
      <div className="text-center mt-5">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (!hackathon) {
    return <p className="text-danger">{errors.fetch}</p>;
  }

  return (
    <div className="container mt-5">
      <h2>{existingSubmission ? 'Edit Project for' : 'Submit Project for'} {hackathon.title}</h2>
      {errors.submit && <p className="text-danger">{errors.submit}</p>}
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Idea:</label>
          <input
            type="text"
            name="idea"
            className={`form-control ${errors.idea ? 'is-invalid' : ''}`}
            value={formData.idea}
            onChange={handleChange}
          />
          {errors.idea && <div className="invalid-feedback">{errors.idea}</div>}
        </div>
        <div className="mb-3">
          <label className="form-label">Description:</label>
          <textarea
            name="description"
            className={`form-control ${errors.description ? 'is-invalid' : ''}`}
            value={formData.description}
            onChange={handleChange}
          />
          {errors.description && <div className="invalid-feedback">{errors.description}</div>}
        </div>
        <div className="mb-3">
          <label className="form-label">Wallet Address:</label>
          {formData.public_key ? (
            <p>{formData.public_key}</p>
          ) : (
            <>
              <button type="button" className="btn btn-secondary" onClick={handleConnectWallet}>
                Connect Wallet
              </button>
              {errors.wallet && <p className="text-danger">{errors.wallet}</p>}
            </>
          )}
          {errors.public_key && <p className="text-danger">{errors.public_key}</p>}
        </div>
        <div className="mb-3">
          <label className="form-label">Number of Teammates:</label>
          <input
            type="number"
            name="number_of_teammates"
            className={`form-control ${errors.number_of_teammates ? 'is-invalid' : ''}`}
            value={formData.number_of_teammates}
            onChange={handleChange}
            min="0"
          />
          {errors.number_of_teammates && <div className="invalid-feedback">{errors.number_of_teammates}</div>}
        </div>
        <div className="mb-3">
          <label className="form-label">Teammate Names (comma-separated):</label>
          <input
            type="text"
            name="teammate_names"
            className="form-control"
            value={formData.teammate_names}
            onChange={handleChange}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">GitHub Link:</label>
          <input
            type="url"
            name="github_link"
            className={`form-control ${errors.github_link ? 'is-invalid' : ''}`}
            value={formData.github_link}
            onChange={handleChange}
          />
          {errors.github_link && <div className="invalid-feedback">{errors.github_link}</div>}
        </div>
        <button type="submit" className="btn btn-primary" disabled={loading || submitting}>
          {loading ? (
            <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
          ) : (
            existingSubmission ? 'Update Project' : 'Submit Project'
          )}
        </button>
      </form>

      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Confirm Submission</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Are you sure you want to {existingSubmission ? 'update' : 'submit'} your project for {hackathon.title}?
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)} disabled={submitting}>
            Cancel
          </Button>
          <Button variant="primary" onClick={confirmSubmit} disabled={submitting}>
            {submitting ? (
              <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
            ) : (
              'Confirm'
            )}
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default SubmitProjectPage;
