// src/pages/HackathonDetailsPage.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';

const HackathonDetailsPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [hackathon, setHackathon] = useState(null);
  const [participants, setParticipants] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchHackathon = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          toast.error('Please log in to view hackathon details');
          navigate('/login');
          return;
        }

        const response = await axios.get(`http://localhost:3000/api/hackathons/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setHackathon(response.data);

        // Fetch participants (if applicable)
        const participantsResponse = await axios.get(`http://localhost:3000/api/hackathons/${id}/participants`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setParticipants(participantsResponse.data || []);
      } catch (error) {
        console.error('Error fetching hackathon:', error);
        toast.error(error.response?.data?.error || 'Failed to fetch hackathon details');
      } finally {
        setLoading(false);
      }
    };

    fetchHackathon();
  }, [id, navigate]);

  if (loading) {
    return <div className="text-center text-secondary">Loading...</div>;
  }

  if (!hackathon) {
    return <div className="text-center text-secondary">Hackathon not found</div>;
  }

  const isOrganizer = localStorage.getItem('role') === 'organizer'; // Adjust based on your auth logic

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Hackathon Details</h1>

      {/* Grey Box: Hackathon Details */}
      <div className="bg-gray-800 p-6 rounded-lg shadow-lg">
        <h2 className="text-xl font-semibold">{hackathon.title}</h2>
        <p className="text-gray-400 mt-2">{hackathon.description}</p>
        <p className="mt-2"><strong>Start Date:</strong> {new Date(hackathon.start_date).toLocaleDateString()}</p>
        <p className="mt-1"><strong>End Date:</strong> {new Date(hackathon.end_date).toLocaleDateString()}</p>
        <p className="mt-1"><strong>Prize Pool:</strong> {hackathon.prize_pool} ETH</p>
        <p className="mt-1"><strong>Participants:</strong> {participants.length}</p>
        <p className="mt-1"><strong>Submissions:</strong> {hackathon.submissions_count || 0}</p>
        <p className="mt-1">
          <strong>Status:</strong>{' '}
          <span className={hackathon.manually_ended ? 'text-red-500' : 'text-green-500'}>
            {hackathon.manually_ended ? 'ENDED' : 'ACTIVE'}
          </span>
        </p>
        <p className="mt-1">
          <strong>Funded:</strong> {hackathon.funded_amount || 0} ETH on{' '}
          {new Date(hackathon.funded_at).toLocaleString()}
        </p>
        {/* Removed the "View Details" button */}
      </div>

      {/* Organizer Actions Section */}
      {isOrganizer && (
        <div className="mt-6">
          <h3 className="text-lg font-medium">Organizer Actions</h3>
          <div className="mt-2 flex gap-3">
            <button
              className="btn btn-primary"
              onClick={() => navigate(`/hackathons/${id}/submissions`)}
            >
              View Submissions
            </button>
            {/* Add other organizer actions here */}
          </div>
        </div>
      )}

      {/* Participants Section */}
      <div className="mt-6">
        <h3 className="text-lg font-medium">Participants ({participants.length})</h3>
        {participants.length === 0 ? (
          <p className="text-gray-400 mt-2">No participants yet.</p>
        ) : (
          <ul className="list-group mt-2">
            {participants.map((participant, index) => (
              <li key={index} className="list-group-item">
                {participant.email || 'Anonymous Participant'}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default HackathonDetailsPage;
