import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';

const SubmissionTable = ({ submissions, hackathon, onSetWinners }) => {
  const [winners, setWinners] = useState({
    first: null,
    second: null,
    third: null,
  });
  const [loading, setLoading] = useState(false);

  // Log hackathon prop changes to ensure itâ€™s updating
  useEffect(() => {
    console.log('Hackathon prop updated:', hackathon);
  }, [hackathon]);

  const handleWinnerChange = (publicKey, position) => {
    setWinners(prev => {
      const newWinners = { ...prev };
      if (newWinners.first === publicKey && position !== 'first') newWinners.first = null;
      if (newWinners.second === publicKey && position !== 'second') newWinners.second = null;
      if (newWinners.third === publicKey && position !== 'third') newWinners.third = null;
      newWinners[position] = publicKey;
      return newWinners;
    });
  };

  const handleEndHackathon = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `http://localhost:3000/api/hackathons/${hackathon.id}/end`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      toast.success(`Hackathon ended! Transaction Hash: ${response.data.transactionHash}`);
      onSetWinners();
    } catch (error) {
      console.error('Error ending hackathon:', error);
      toast.error(error.response?.data?.error || 'Failed to end hackathon');
    } finally {
      setLoading(false);
    }
  };

  const handleFundHackathon = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const amount = prompt('Enter funding amount in ETH:');
      if (!amount || isNaN(amount) || Number(amount) <= 0) {
        toast.error('Please enter a valid amount');
        setLoading(false);
        return;
      }

      const response = await axios.post(
        `http://localhost:3000/api/hackathons/${hackathon.id}/fund`,
        { amount },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      toast.success(`Hackathon funded! Transaction Hash: ${response.data.transactionHash}`);
      onSetWinners();
    } catch (error) {
      console.error('Error funding hackathon:', error);
      toast.error(error.response?.data?.error || 'Failed to fund hackathon');
    } finally {
      setLoading(false);
    }
  };

  const handleSetWinnersAndDistribute = async () => {
    const selectedWinners = [winners.first, winners.second, winners.third].filter(w => w !== null);
    if (selectedWinners.length === 0) {
      toast.error('Please select at least one winner.');
      return;
    }

    setLoading(true);
    try {
      const token = localStorage.getItem('token');

      const setWinnersResponse = await axios.post(
        `http://localhost:3000/api/hackathons/${hackathon.id}/set-winners`,
        { winners: selectedWinners },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      toast.success(`Winners set! Transaction Hash: ${setWinnersResponse.data.transactionHash}`);

      const distributeResponse = await axios.post(
        `http://localhost:3000/api/hackathons/${hackathon.id}/distribute`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      toast.success(`Prizes distributed! Transaction Hash: ${distributeResponse.data.transactionHash}`);

      onSetWinners();
    } catch (error) {
      console.error('Error setting winners and distributing prizes:', error);
      toast.error(error.response?.data?.error || 'Failed to set winners and distribute prizes');
    } finally {
      setLoading(false);
    }
  };

  // Detailed debug logs
  console.log('Raw hackathon values:', {
    manually_ended: hackathon.manually_ended,
    funded_amount: hackathon.funded_amount,
    prizes_distributed: hackathon.prizes_distributed,
    typeof_manually_ended: typeof hackathon.manually_ended,
    typeof_funded_amount: typeof hackathon.funded_amount,
    typeof_prizes_distributed: typeof hackathon.prizes_distributed,
  });
  const fundCondition = Boolean(hackathon.manually_ended) && (hackathon.funded_amount === null || hackathon.funded_amount === 0) && !Boolean(hackathon.prizes_distributed);
  const setWinnersCondition = Boolean(hackathon.manually_ended) && hackathon.funded_amount > 0 && !Boolean(hackathon.prizes_distributed);
  console.log('Fund Hackathon button should show:', fundCondition);
  console.log('Set Winners & Distribute button should show:', setWinnersCondition);

  return (
    <div>
      {submissions.length === 0 ? (
        <p>No submissions yet.</p>
      ) : (
        <>
          <table className="table table-striped">
            <thead>
              <tr>
                <th>Submitter</th>
                <th>Idea</th>
                <th>Description</th>
                <th>Public Key</th>
                <th>Teammates</th>
                <th>GitHub Link</th>
                <th>Position</th>
              </tr>
            </thead>
            <tbody>
              {submissions.map(submission => (
                <tr key={submission.id}>
                  <td>{submission.email || submission.submitter_email}</td>
                  <td>{submission.idea}</td>
                  <td>{submission.description}</td>
                  <td>{submission.public_key}</td>
                  <td>{submission.teammate_names || 'None'}</td>
                  <td>
                    {submission.github_link ? (
                      <a href={submission.github_link} target="_blank" rel="noopener noreferrer">GitHub</a>
                    ) : 'N/A'}
                  </td>
                  <td>
                    <select
                      value={
                        winners.first === submission.public_key ? 'first' :
                        winners.second === submission.public_key ? 'second' :
                        winners.third === submission.public_key ? 'third' : ''
                      }
                      onChange={(e) => handleWinnerChange(submission.public_key, e.target.value)}
                      disabled={hackathon.prizes_distributed}
                    >
                      <option value="">None</option>
                      <option value="first">1st</option>
                      <option value="second">2nd</option>
                      <option value="third">3rd</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* End Hackathon Button */}
          {!hackathon.manually_ended && (
            <button
              className="btn btn-primary me-2"
              onClick={handleEndHackathon}
              disabled={loading}
            >
              {loading ? 'Ending...' : 'End Hackathon'}
            </button>
          )}

          {/* Fund Hackathon Button */}
          {fundCondition && (
            <button
              className="btn btn-primary me-2"
              onClick={handleFundHackathon}
              disabled={loading}
            >
              {loading ? 'Funding...' : 'Fund Hackathon'}
            </button>
          )}

          {/* Set Winners & Distribute Button */}
          {setWinnersCondition && (
            <button
              className="btn btn-success me-2"
              onClick={handleSetWinnersAndDistribute}
              disabled={loading}
            >
              {loading ? 'Processing...' : 'Set Winners & Distribute'}
            </button>
          )}

          {hackathon.winners && hackathon.winners.length > 0 && (
            <div className="mt-3">
              <h4>Winners:</h4>
              <ul>
                {hackathon.winners.map((winner, index) => (
                  <li key={index}>
                    {index === 0 ? '1st' : index === 1 ? '2nd' : '3rd'}: {winner.public_key}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default SubmissionTable;
