import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import HackathonCard from '../components/HackathonCard';
import axios from 'axios';
import { toast } from 'react-toastify';

const OrganizerHackathonsPage = () => {
  const [hackathons, setHackathons] = useState([]);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchHackathons = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        const response = await axios.get('http://localhost:3000/api/organizer/hackathons', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setHackathons(response.data);
      } catch (error) {
        console.error('Fetch hackathons error:', error);
        toast.error('Failed to fetch hackathons');
      } finally {
        setLoading(false);
      }
    };

    fetchHackathons();
  }, [navigate]);

  const handleFund = async (id) => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `http://localhost:3000/api/hackathons/${id}/fund`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      toast.success(response.data.message);
      setHackathons(hackathons.map(h => 
        h.id === id ? { ...h, funded_amount: h.prize_pool, funded_at: new Date().toISOString() } : h
      ));
    } catch (error) {
      console.error('Fund hackathon error:', error);
      toast.error('Failed to fund hackathon');
    } finally {
      setLoading(false);
    }
  };

  const handleEnd = async (id) => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `http://localhost:3000/api/hackathons/${id}/end`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      toast.success(response.data.message);
      setHackathons(hackathons.map(h => 
        h.id === id ? { ...h, status: 'ended', manually_ended: true, manually_ended_at: new Date().toISOString() } : h
      ));
    } catch (error) {
      console.error('End hackathon error:', error);
      toast.error('Failed to end hackathon');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="text-center mt-6">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-6">
      <h2 className="text-3xl font-bold bg-gradient-to-r from-indigo-400 to-purple-400 bg-clip-text text-transparent mb-6">
        My Hackathons
      </h2>
      {hackathons.length === 0 ? (
        <p className="text-gray-400">No hackathons created yet.</p>
      ) : (
        hackathons.map((hackathon) => (
          <HackathonCard
            key={hackathon.id}
            hackathon={hackathon}
            user={user}
            isOrganizer={true}
            onFund={handleFund}
            onEnd={handleEnd}
          />
        ))
      )}
    </div>
  );
};

export default OrganizerHackathonsPage;
