// HackathonDetailsPage.jsx
import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import HackathonCard from '../components/HackathonCard';
import * as hackathonService from '../services/hackathonService';

const HackathonDetailsPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [hackathon, setHackathon] = useState(null);
  const [participants, setParticipants] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const isOrganizer = user?.role === 'organizer' && hackathon?.organizer_id === user.id;
  const isParticipant = participants.some(p => p.id === user.id);

  const fetchData = useCallback(async () => {
    if (!id) return;

    setLoading(true);
    setError(null);

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      const [hackathonData, participantsData] = await Promise.all([
        hackathonService.getHackathon(id, token),
        hackathonService.getParticipants(id, token)
      ]);

      setHackathon(hackathonData);
      setParticipants(participantsData);
    } catch (err) {
      const errorMessage = err.message || 'Failed to load hackathon details';
      setError(errorMessage);
      toast.error(errorMessage);
      navigate('/hackathons');
    } finally {
      setLoading(false);
    }
  }, [id, navigate]);

  useEffect(() => {
    let mounted = true;

    fetchData().then(() => {
      if (!mounted) {
        setHackathon(null);
        setParticipants([]);
      }
    });

    return () => {
      mounted = false;
    };
  }, [fetchData]);

  const handleJoin = async () => {
    if (!id) return;

    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      await hackathonService.joinHackathon(id, token);
      toast.success('Joined hackathon successfully!');
      setParticipants(prev => [...prev, { id: user.id, email: user.email }]);
    } catch (error) {
      const errorMsg = error.response?.data?.error || 'Unknown error';
      toast.error(`Failed to join hackathon: ${errorMsg}`);
    } finally {
      setLoading(false);
    }
  };

  const handleWithdraw = async () => {
    if (!id) return;

    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      await hackathonService.withdrawHackathon(id, token);
      toast.success('Withdrawn from hackathon successfully!');
      setParticipants(prev => prev.filter(p => p.id !== user.id));
    } catch (error) {
      const errorMsg = error.response?.data?.error || 'Unknown error';
      toast.error(`Failed to withdraw: ${errorMsg}`);
    } finally {
      setLoading(false);
    }
  };

  const handleEndHackathon = async () => {
    if (!id || !window.confirm('Are you sure you want to end this hackathon?')) return;

    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      await hackathonService.endHackathon(id, token);
      toast.success('Hackathon ended successfully!');
      setHackathon(prev => ({
        ...prev,
        status: 'ended',
        manually_ended: true,
        manually_ended_at: new Date().toISOString()
      }));
      setTimeout(() => window.location.reload(), 1500);
    } catch (error) {
      const errorMsg = error.response?.data?.error || 'Unknown error';
      toast.error(`Failed to end hackathon: ${errorMsg}`);
    } finally {
      setLoading(false);
    }
  };

  const handleFundHackathon = async () => {
    if (!id || !window.confirm('Are you sure you want to fund this hackathon?')) return;

    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      await hackathonService.fundHackathon(id, token);
      toast.success('Hackathon funded successfully!');
      setHackathon(prev => ({
        ...prev,
        funded_amount: prev.prize_pool,
        funded_at: new Date().toISOString()
      }));
      setTimeout(() => window.location.reload(), 1500);
    } catch (error) {
      const errorMsg = error.response?.data?.error || 'Unknown error';
      toast.error(`Failed to fund hackathon: ${errorMsg}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="hackathon-details">
      {loading && (
        <div className="loading" aria-live="polite">
          Loading hackathon details...
        </div>
      )}

      {error && (
        <div className="error" role="alert">
          {error}
        </div>
      )}

      {!loading && !error && hackathon && (
        <div className="hackathon-content">
          <HackathonCard hackathon={hackathon} />

          <section className="participants" aria-label="Participants list">
            <h3>Participants ({participants.length})</h3>
            {participants.length > 0 ? (
              <ul>
                {participants.map(participant => (
                  <li key={participant.id}>{participant.email}</li>
                ))}
              </ul>
            ) : (
              <p>No participants yet</p>
            )}
          </section>

          <section className="actions" aria-label="Hackathon actions">
            {!isParticipant && hackathon.status === 'active' && (
              <button 
                onClick={handleJoin} 
                disabled={loading}
                aria-label="Join this hackathon"
              >
                Join Hackathon
              </button>
            )}

            {isParticipant && hackathon.status === 'active' && (
              <button 
                onClick={handleWithdraw} 
                disabled={loading}
                aria-label="Withdraw from this hackathon"
              >
                Withdraw
              </button>
            )}

            {isOrganizer && hackathon.status !== 'ended' && (
              <div className="organizer-actions">
                <button 
                  onClick={handleEndHackathon} 
                  disabled={loading}
                  aria-label="End this hackathon"
                >
                  End Hackathon
                </button>

                {!hackathon.funded_at && (
                  <button 
                    onClick={handleFundHackathon} 
                    disabled={loading}
                    aria-label="Fund this hackathon"
                  >
                    Fund Hackathon
                  </button>
                )}

                <Link 
                  to={`/hackathons/${id}/edit`}
                  aria-label="Edit hackathon details"
                >
                  Edit Hackathon
                </Link>
              </div>
            )}
          </section>
        </div>
      )}

      {!loading && !error && !hackathon && (
        <div className="not-found" role="alert">
          Hackathon not found
        </div>
      )}
    </div>
  );
};

export default HackathonDetailsPage;
