import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import SubmissionTable from '../components/SubmissionTable';
import { toast } from 'react-toastify';

const SubmissionsPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [submissions, setSubmissions] = useState([]);
  const [hackathon, setHackathon] = useState(null);
  const [loading, setLoading] = useState(false);
  const [step, setStep] = useState(1); // Tracks current workflow step

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        const hackathonResponse = await axios.get(`http://localhost:3000/api/hackathons/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setHackathon(hackathonResponse.data);

        // Set current step based on hackathon state
        if (hackathonResponse.data.prizes_distributed) {
          setStep(4);
        } else if (hackathonResponse.data.winners && 
                  JSON.parse(hackathonResponse.data.winners).length > 0) {
          setStep(3);
        } else if (hackathonResponse.data.funded_amount > 0) {
          setStep(2);
        } else if (hackathonResponse.data.manually_ended) {
          setStep(1);
        }

        const submissionsResponse = await axios.get(`http://localhost:3000/api/hackathons/${id}/submissions`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setSubmissions(submissionsResponse.data);
      } catch (error) {
        console.error('Fetch data error:', error);
        toast.error('Failed to fetch data');
        navigate('/hackathons');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id, navigate]);

  const handleSetWinners = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`http://localhost:3000/api/hackathons/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setHackathon(response.data);

      // Update step if winners were set
      if (response.data.winners && JSON.parse(response.data.winners).length > 0) {
        setStep(3);
      }
    } catch (error) {
      console.error('Fetch updated hackathon error:', error);
      toast.error('Failed to refresh hackathon data');
    } finally {
      setLoading(false);
    }
  };

  const handleDistributePrizes = async () => {
    if (!hackathon.winners || JSON.parse(hackathon.winners).length === 0) {
      toast.error('Please select winners first');
      return;
    }

    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `http://localhost:3000/api/hackathons/${id}/distribute`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      toast.success(`Prizes distributed! Transaction Hash: ${response.data.transactionHash}`);

      // Update hackathon data and step
      const updatedHackathon = await axios.get(`http://localhost:3000/api/hackathons/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setHackathon(updatedHackathon.data);
      setStep(4);
    } catch (error) {
      console.error('Error distributing prizes:', error);
      toast.error(error.response?.data?.error || 'Failed to distribute prizes');
    } finally {
      setLoading(false);
    }
  };

  const goToFundHackathon = () => {
    navigate(`/hackathons/${id}`);
  };

  if (loading && !hackathon) {
    return (
      <div className="text-center mt-5">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (!hackathon) {
    return <p>Loading...</p>;
  }

  return (
    <div className="container mt-5">
      <h2>Submissions for {hackathon.title}</h2>

      {/* Hackathon workflow status tracker */}
      <div className="alert alert-info mb-4">
        <h4>Hackathon Prize Distribution Workflow</h4>
        <ol className="mb-0">
          <li className={hackathon.manually_ended ? "text-success fw-bold" : ""}>
            End Hackathon {hackathon.manually_ended && "✓"}
          </li>
          <li className={hackathon.funded_amount > 0 ? "text-success fw-bold" : ""}>
            Fund Hackathon {hackathon.funded_amount > 0 && "✓"}
            {hackathon.manually_ended && !hackathon.funded_amount && (
              <button 
                className="btn btn-sm btn-primary ms-2" 
                onClick={goToFundHackathon}
              >
                Go to Fund
              </button>
            )}
          </li>
          <li className={hackathon.winners && JSON.parse(hackathon.winners).length > 0 ? "text-success fw-bold" : ""}>
            Select Winners {hackathon.winners && JSON.parse(hackathon.winners).length > 0 && "✓"}
          </li>
          <li className={hackathon.prizes_distributed ? "text-success fw-bold" : ""}>
            Distribute Prizes {hackathon.prizes_distributed && "✓"}
            {hackathon.winners && 
             JSON.parse(hackathon.winners).length > 0 && 
             !hackathon.prizes_distributed && (
              <button 
                className="btn btn-sm btn-success ms-2" 
                onClick={handleDistributePrizes}
                disabled={loading}
              >
                Distribute Now
              </button>
            )}
          </li>
        </ol>
      </div>

      {/* Show current step instructions */}
      {step === 1 && !hackathon.manually_ended && (
        <div className="alert alert-warning">
          First, you need to end the hackathon from the hackathon details page.
        </div>
      )}

      {step === 1 && hackathon.manually_ended && !hackathon.funded_amount && (
        <div className="alert alert-warning">
          The hackathon is ended. Next, fund the hackathon from the details page.
        </div>
      )}

      {step === 2 && (
        <div className="alert alert-warning">
          The hackathon is funded. Now select winners from the submissions below.
        </div>
      )}

      {step === 3 && (
        <div className="alert alert-warning">
          Winners selected! Click "Distribute Prizes" to send ETH to the winners.
        </div>
      )}

      {step === 4 && (
        <div className="alert alert-success">
          Prizes have been distributed! The hackathon is complete.
        </div>
      )}

      {/* Submission table component */}
      <SubmissionTable
        submissions={submissions}
        hackathon={hackathon}
        onSetWinners={handleSetWinners}
        onDistributePrizes={handleDistributePrizes}
      />
    </div>
  );
};

export default SubmissionsPage;
