import React, { useRef, useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { motion, useScroll, useTransform, AnimatePresence } from 'framer-motion';
import { ArrowRight, Calendar, Code2, Trophy, Users, ChevronDown, ExternalLink, Github, Globe, Wallet } from 'lucide-react';

const LandingPage = () => {
  const user = JSON.parse(localStorage.getItem('user'));
  const heroRef = useRef(null);
  const featuresRef = useRef(null);
  const [activeTab, setActiveTab] = useState('all');
  const [isScrolled, setIsScrolled] = useState(false);
  const [showWalletDropdown, setShowWalletDropdown] = useState(false);

  const { scrollYProgress } = useScroll({
    target: heroRef,
    offset: ["start start", "end start"],
  });

  // Parallax effects
  const opacity = useTransform(scrollYProgress, [0, 0.5], [1, 0]);
  const scale = useTransform(scrollYProgress, [0, 0.5], [1, 0.9]);
  const position = useTransform(scrollYProgress, (pos) => pos === 1 ? "relative" : "fixed");

  // Navbar scroll effect
  useEffect(() => {
    const handleScroll = () => setIsScrolled(window.scrollY > 10);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Hackathon data
  const hackathons = [
    { id: 1, title: "EthGlobal Paris", date: "April 15-17, 2025", location: "Paris, France", totalPrize: "$50,000", participants: 412, category: "ethereum", image: "/api/placeholder/400/220" },
    { id: 2, title: "Solana Summer Hack", date: "May 20-23, 2025", location: "Virtual", totalPrize: "$75,000", participants: 358, category: "solana", image: "/api/placeholder/400/220" },
    { id: 3, title: "DeFi Developer Summit", date: "June 5-8, 2025", location: "Berlin, Germany", totalPrize: "$100,000", participants: 524, category: "defi", image: "/api/placeholder/400/220" },
  ];

  // Features data
  const features = [
    { icon: <Calendar className="h-6 w-6" />, title: "Upcoming Hackathons", description: "Discover global hackathons with real-time updates and reminders." },
    { icon: <Users className="h-6 w-6" />, title: "Team Building", description: "Match with skilled teammates based on expertise and goals." },
    { icon: <Code2 className="h-6 w-6" />, title: "Project Submissions", description: "Submit projects with integrated GitHub and feedback tools." },
    { icon: <Trophy className="h-6 w-6" />, title: "Prizes & Recognition", description: "Win ETH prizes and earn verified Web3 credentials." },
  ];

  // Stats data
  const stats = [
    { value: "50+", label: "Hackathons Hosted" },
    { value: "10K+", label: "Developers Joined" },
    { value: "$2M+", label: "Prizes Awarded" },
    { value: "500+", label: "Projects Built" },
  ];

  const filteredHackathons = activeTab === 'all' ? hackathons : hackathons.filter(h => h.category === activeTab);

  return (
    <div className="min-h-screen bg-black text-white font-sans overflow-x-hidden">
      {/* Header */}
      <motion.header
        className={`fixed top-0 w-full z-50 px-6 py-4 transition-all duration-300 ${isScrolled ? 'bg-black/80 backdrop-blur-md shadow-md border-b border-gray-800' : 'bg-transparent'}`}
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <div className="container mx-auto flex items-center justify-between">
          <div className="flex items-center space-x-10">
            <Link to="/" className="text-2xl font-extrabold bg-gradient-to-r from-indigo-500 to-purple-500 bg-clip-text text-transparent">blockHUNT</Link>
            <nav className="hidden md:flex space-x-6">
              {['hackathons', 'projects', 'teams', 'docs'].map((item) => (
                <Link key={item} to={`/${item}`} className="text-gray-300 hover:text-indigo-400 transition-colors duration-200 capitalize relative group">
                  {item}
                  <span className="absolute -bottom-1 left-0 w-0 h-0.5 bg-indigo-500 transition-all duration-300 group-hover:w-full"></span>
                </Link>
              ))}
            </nav>
          </div>
          <div className="flex items-center space-x-4">
            {user ? (
              <div className="relative">
                <button
                  onClick={() => setShowWalletDropdown(!showWalletDropdown)}
                  className="flex items-center space-x-2 bg-gray-900/50 border border-indigo-500/30 hover:border-indigo-500/70 rounded-full py-2 px-4 text-gray-200 hover:text-white transition-all"
                >
                  <Wallet className="h-5 w-5 text-indigo-400" />
                  <span className="hidden sm:inline text-sm">0x8a1f...0C64</span>
                  <ChevronDown className={`h-4 w-4 transition-transform ${showWalletDropdown ? 'rotate-180' : ''}`} />
                </button>
                <AnimatePresence>
                  {showWalletDropdown && (
                    <motion.div
                      initial={{ opacity: 0, y: 10 }}
                      animate={{ opacity: 1, y: 0 }}
                      exit={{ opacity: 0, y: 10 }}
                      className="absolute right-0 mt-2 w-48 bg-gray-900 border border-gray-800 rounded-lg shadow-xl"
                    >
                      {['profile', 'dashboard', 'settings'].map((item) => (
                        <Link key={item} to={`/${item}`} className="block px-4 py-2 text-sm text-gray-300 hover:bg-indigo-600/20 hover:text-white capitalize">
                          {item}
                        </Link>
                      ))}
                      <div className="border-t border-gray-800" />
                      <Link to="/logout" className="block px-4 py-2 text-sm text-red-400 hover:bg-red-600/20 hover:text-red-300">Logout</Link>
                    </motion.div>
                  )}
                </AnimatePresence>
              </div>
            ) : (
              <div className="flex space-x-4">
                <Link to="/login" className="text-gray-300 hover:text-indigo-400 text-sm font-medium transition-colors">Login</Link>
                <Link to="/signup" className="bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 text-white px-4 py-2 rounded-full text-sm font-medium shadow-md">Sign Up</Link>
              </div>
            )}
          </div>
        </div>
      </motion.header>

      {/* Hero Section */}
      <section ref={heroRef} className="relative min-h-screen flex items-center justify-center overflow-hidden">
        <motion.div style={{ opacity, scale, position }} className="absolute inset-0 w-full h-full z-0">
          <div className="absolute inset-0 bg-gradient-to-br from-indigo-900/30 via-black to-purple-900/30" />
          {[...Array(20)].map((_, i) => (
            <motion.div
              key={i}
              className="absolute bg-indigo-500/20 rounded-full"
              style={{ width: '4px', height: '4px', x: `${Math.random() * 100}%`, y: `${Math.random() * 100}%` }}
              animate={{ opacity: [0.2, 0.6, 0.2], scale: [0.5, 1, 0.5] }}
              transition={{ duration: 5 + Math.random() * 5, repeat: Infinity }}
            />
          ))}
        </motion.div>
        <div className="container relative z-10 mx-auto px-6 py-32 text-center">
          <motion.span
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
