import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ethers } from 'ethers';
import { toast } from 'react-toastify';
import axios from 'axios';

const Navbar = () => {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const user = JSON.parse(localStorage.getItem('user'));
  const [walletAddress, setWalletAddress] = useState(user?.wallet_address || '');

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    toast.success('Logged out successfully!');
    navigate('/login');
  };

  const handleConnectWallet = async () => {
    try {
      if (!window.ethereum) {
        toast.error('Please install MetaMask to connect your wallet');
        return;
      }

      const provider = new ethers.BrowserProvider(window.ethereum);
      await provider.send('eth_requestAccounts', []);
      const signer = await provider.getSigner();
      const address = await signer.getAddress();

      const token = localStorage.getItem('token');
      await axios.post(
        'http://localhost:3000/api/connect-wallet',
        { wallet_address: address },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setWalletAddress(address);
      localStorage.setItem('user', JSON.stringify({ ...user, wallet_address: address }));
      toast.success('Wallet connected successfully!');
    } catch (err) {
      console.error('Connect wallet error:', err);
      toast.error('Failed to connect wallet');
    }
  };

  return (
    <nav className="bg-gray-900/50 border-b border-indigo-500/20 py-4">
      <div className="container flex items-center justify-between">
        {/* Brand */}
        <Link
          to="/"
          className="text-2xl font-bold bg-gradient-to-r from-indigo-400 to-purple-400 bg-clip-text text-transparent"
        >
          blockHUNT
        </Link>

        {/* Navigation Links */}
        <div className="flex items-center space-x-4">
          <Link to="/hackathons" className="text-gray-300 hover:text-indigo-400 transition">
            Hackathons
          </Link>

          {token && user && user.role === 'organizer' && (
            <>
              <Link to="/create-hackathon" className="text-gray-300 hover:text-indigo-400 transition">
                Create Hackathon
              </Link>
              <Link to="/organizer-dashboard" className="text-gray-300 hover:text-indigo-400 transition">
                Dashboard
              </Link>
              <Link to="/organizer-hackathons" className="text-gray-300 hover:text-indigo-400 transition">
                My Hackathons
              </Link>
            </>
          )}

          {/* Right Side: Wallet, Profile, Logout/Login/Signup */}
          {token ? (
            <>
              {user.role === 'organizer' && !walletAddress && (
                <button className="btn-gradient" onClick={handleConnectWallet}>
                  Connect Wallet
                </button>
              )}
              {user.role === 'organizer' && walletAddress && (
                <span className="text-gray-300">
                  Wallet: {walletAddress.slice(0, 6)}...{walletAddress.slice(-4)}
                </span>
              )}
              <Link to="/user-profile" className="text-gray-300 hover:text-indigo-400 transition">
                Profile
              </Link>
              <button className="btn-gradient bg-red-600 hover:bg-red-700" onClick={handleLogout}>
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="text-gray-300 hover:text-indigo-400 transition">
                Login
              </Link>
              <Link to="/signup" className="btn-gradient">
                Signup
              </Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
