import React, { useState, useEffect } from 'react';
import HackathonCard from '../components/HackathonCard';
import axios from 'axios';
import { toast } from 'react-toastify';

const HackathonsPage = () => {
  const [hackathons, setHackathons] = useState([]);
  const [loading, setLoading] = useState(false);
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchHackathons = async () => {
      setLoading(true);
      try {
        const response = await axios.get('http://localhost:3000/api/hackathons');
        // Sort hackathons - active ones first
        const sortedHackathons = response.data.sort((a, b) => {
          // If a is active and b is not, a comes first
          if (a.status === 'active' && b.status !== 'active') return -1;
          // If b is active and a is not, b comes first
          if (b.status === 'active' && a.status !== 'active') return 1;
          // Otherwise, maintain original order
          return 0;
        });
        setHackathons(sortedHackathons);
      } catch (err) {
        console.error('Fetch hackathons error:', err);
        toast.error('Failed to load hackathons');
      } finally {
        setLoading(false);
      }
    };
    fetchHackathons();
  }, []);

  // Make sure any modifications to hackathons maintain the sorting
  const updateHackathon = (id, updates) => {
    const updatedHackathons = hackathons.map(h => 
      h.id === id ? { ...h, ...updates } : h
    );

    // Re-sort after updating to maintain active-first order
    const sortedUpdatedHackathons = updatedHackathons.sort((a, b) => {
      if (a.status === 'active' && b.status !== 'active') return -1;
      if (b.status === 'active' && a.status !== 'active') return 1;
      return 0;
    });

    setHackathons(sortedUpdatedHackathons);
  };

  // Split hackathons into active and non-active for separate rendering
  const activeHackathons = hackathons.filter(h => h.status === 'active');
  const otherHackathons = hackathons.filter(h => h.status !== 'active');

  if (loading) {
    return (
      <div className="text-center mt-6">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  // Function to render hackathons in a grid (two per row)
  const renderHackathonGrid = (hackathonList, sectionTitle) => {
    if (hackathonList.length === 0) return null;

    return (
      <div className="mb-5">
        {sectionTitle && <h3 className="text-xl font-medium mb-3">{sectionTitle}</h3>}
        <div className="row row-cols-1 row-cols-md-2 g-4">
          {hackathonList.map((hackathon) => (
            <div className="col" key={hackathon.id}>
              <HackathonCard
                hackathon={hackathon}
                user={user}
                isOrganizer={user?.role === 'organizer' && user?.id === hackathon.organizer_id}
                onFund={(id) => {
                  updateHackathon(id, {
                    funded_amount: hackathon.prize_pool,
                    funded_at: new Date().toISOString()
                  });
                }}
                onEnd={(id) => {
                  updateHackathon(id, {
                    status: 'ended',
                    manually_ended: true,
                    manually_ended_at: new Date().toISOString()
                  });
                }}
              />
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <div className="container mt-6">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="text-2xl font-semibold">Hackathons</h2>
        {user?.role === 'organizer' && (
          <a href="/hackathons/create" className="btn btn-primary">
            Create Hackathon
          </a>
        )}
      </div>

      {hackathons.length === 0 ? (
        <div className="alert alert-info">
          <p className="mb-0">No hackathons available.</p>
        </div>
      ) : (
        <>
          {renderHackathonGrid(activeHackathons, "Active Hackathons")}
          {renderHackathonGrid(otherHackathons, "Other Hackathons")}
        </>
      )}
    </div>
  );
};

export default HackathonsPage;
