import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ethers } from 'ethers';
import { toast } from 'react-toastify';
import axios from 'axios';

const Navbar = () => {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const user = JSON.parse(localStorage.getItem('user'));
  const [walletAddress, setWalletAddress] = useState(user?.wallet_address || '');

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    toast.success('Logged out successfully!');
    navigate('/login');
  };

  const handleConnectWallet = async () => {
    try {
      if (!window.ethereum) {
        toast.error('Please install MetaMask to connect your wallet');
        return;
      }

      const provider = new ethers.BrowserProvider(window.ethereum);
      await provider.send('eth_requestAccounts', []);
      const signer = await provider.getSigner();
      const address = await signer.getAddress();

      const token = localStorage.getItem('token');
      await axios.post(
        'http://localhost:3000/api/connect-wallet',
        { wallet_address: address },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setWalletAddress(address);
      localStorage.setItem('user', JSON.stringify({ ...user, wallet_address: address }));
      toast.success('Wallet connected successfully!');
    } catch (err) {
      console.error('Connect wallet error:', err);
      toast.error('Failed to connect wallet');
    }
  };

  return (
    <nav className="bg-gray-900/90 border-b border-indigo-500/20 py-4 sticky top-0 z-50 shadow-lg">
      <div className="container flex items-center justify-between py-3 px-6">
        {/* Brand */}
        <Link
          to="/"
          className="text-3xl font-bold bg-gradient-to-r from-indigo-400 to-purple-400 bg-clip-text text-transparent transition hover:scale-105 flex items-center space-x-3"
        >
          <span className="icon-circle text-white text-xl">🔍</span>
          <span>blockHUNT</span>
        </Link>

        {/* Navigation Links */}
        <div className="flex items-center space-x-6 md:space-x-8">
          <Link
            to="/hackathons"
            className="text-gray-300 font-semibold text-lg hover:text-indigo-400 transition relative group"
          >
            Hackathons
            <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-gradient-to-r from-indigo-400 to-purple-400 transition-all group-hover:w-full"></span>
          </Link>

          {token && user && user.role === 'organizer' && (
            <>
              <Link
                to="/create-hackathon"
                className="text-gray-300 font-semibold text-lg hover:text-indigo-400 transition relative group"
              >
                Create Hackathon
                <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-gradient-to-r from-indigo-400 to-purple-400 transition-all group-hover:w-full"></span>
              </Link>
              <Link
                to="/organizer-dashboard"
                className="text-gray-300 font-semibold text-lg hover:text-indigo-400 transition relative group"
              >
                Dashboard
                <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-gradient-to-r from-indigo-400 to-purple-400 transition-all group-hover:w-full"></span>
              </Link>
              <Link
                to="/organizer-hackathons"
                className="text-gray-300 font-semibold text-lg hover:text-indigo-400 transition relative group"
              >
                My Hackathons
                <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-gradient-to-r from-indigo-400 to-purple-400 transition-all group-hover:w-full"></span>
              </Link>
            </>
          )}

          {/* Right Side: Wallet, Profile, Logout/Login/Signup */}
          {token ? (
            <>
              {user.role === 'organizer' && !walletAddress && (
                <button
                  className="btn-gradient hover:shadow-lg hover:scale-105 transition-all text-lg"
                  onClick={handleConnectWallet}
                >
                  Connect Wallet
                </button>
              )}
              {user.role === 'organizer' && walletAddress && (
                <span className="badge bg-gray-800 border border-indigo-500/50 text-gray-300 px-4 py-1 hover:bg-gray-700 transition text-sm">
                  {walletAddress.slice(0, 6)}...{walletAddress.slice(-4)}
                </span>
              )}
              <Link
                to="/user-profile"
                className="text-gray-300 font-semibold text-lg hover:text-indigo-400 transition relative group"
              >
                Profile
                <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-gradient-to-r from-indigo-400 to-purple-400 transition-all group-hover:w-full"></span>
              </Link>
              <button
                className="btn-gradient bg-gradient-to-r from-red-600 to-red-800 hover:from-red-700 hover:to-red-900 hover:shadow-lg hover:scale-105 transition-all text-lg"
                onClick={handleLogout}
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link
                to="/login"
                className="text-gray-300 font-semibold text-lg hover:text-indigo-400 transition relative group"
              >
                Login
                <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-gradient-to-r from-indigo-400 to-purple-400 transition-all group-hover:w-full"></span>
              </Link>
              <button className="btn-gradient hover:shadow-lg hover:scale-105 transition-all text-lg">
                <Link to="/signup">Signup</Link>
              </button>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
