import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import SubmissionTable from '../components/SubmissionTable';
import { toast } from 'react-toastify';

const SubmissionsPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [submissions, setSubmissions] = useState([]);
  const [hackathon, setHackathon] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        const hackathonResponse = await axios.get(`http://localhost:3000/api/hackathons/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setHackathon(hackathonResponse.data);

        const submissionsResponse = await axios.get(`http://localhost:3000/api/hackathons/${id}/submissions`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setSubmissions(submissionsResponse.data);
      } catch (error) {
        console.error('Fetch data error:', error);
        toast.error('Failed to fetch data');
        navigate('/hackathons');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id, navigate]);

  const handleSetWinners = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`http://localhost:3000/api/hackathons/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setHackathon(response.data);
    } catch (error) {
      console.error('Fetch updated hackathon error:', error);
      toast.error('Failed to refresh hackathon data');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="text-center mt-5">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (!hackathon) {
    return <p>Loading...</p>;
  }

  return (
    <div className="container mt-5">
      <h2>Submissions for {hackathon.title}</h2>
      <SubmissionTable
        submissions={submissions}
        hackathon={hackathon}
        onSetWinners={handleSetWinners}
      />
    </div>
  );
};

export default SubmissionsPage;
