import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const HackathonCard = ({ hackathon, user, isOrganizer, onFund, onEnd }) => {
  const { id, title, description, start_date, end_date, prize_pool, status, funded_amount } = hackathon;

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      className="card mb-4"
    >
      <div className="p-6">
        <div className="flex justify-between items-center mb-3">
          <h3 className="text-2xl font-semibold text-indigo-400">{title}</h3>
          <span
            className={`badge ${
              status === 'active' ? 'bg-green-600' : status === 'ended' ? 'bg-red-600' : 'bg-yellow-400'
            } text-white`}
          >
            {status.charAt(0).toUpperCase() + status.slice(1)}
          </span>
        </div>
        <p className="text-gray-300 mb-4">{description}</p>
        <div className="grid grid-cols-2 gap-4 mb-4">
          <div>
            <p className="text-gray-400">Start Date</p>
            <p className="text-white">{new Date(start_date).toLocaleString()}</p>
          </div>
          <div>
            <p className="text-gray-400">End Date</p>
            <p className="text-white">{new Date(end_date).toLocaleString()}</p>
          </div>
          <div>
            <p className="text-gray-400">Prize Pool</p>
            <p className="text-white">{prize_pool} ETH</p>
          </div>
          <div>
            <p className="text-gray-400">Funded</p>
            <p className="text-white">{funded_amount ? `${funded_amount} ETH` : 'Not Funded'}</p>
          </div>
        </div>
        <div className="flex space-x-4">
          <Link to={`/hackathons/${id}`} className="btn-gradient">
            View Details
          </Link>
          {isOrganizer && status === 'active' && (
            <button onClick={() => onEnd(id)} className="btn-gradient bg-red-600 hover:bg-red-700">
              End Hackathon
            </button>
          )}
          {isOrganizer && status === 'ended' && !funded_amount && (
            <button onClick={() => onFund(id)} className="btn-gradient bg-green-600 hover:bg-green-700">
              Fund Hackathon
            </button>
          )}
        </div>
      </div>
    </motion.div>
  );
};

export default HackathonCard;
