import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';

const HackathonCard = ({ hackathon, user, isOrganizer, onFund, onEnd }) => {
  // Log the hackathon data for debugging
  useEffect(() => {
    console.log('HackathonCard received hackathon:', hackathon);
  }, [hackathon]);

  // Determine the actual status considering both status field and manually_ended flag
  const actualStatus = hackathon.manually_ended ? 'ended' : hackathon.status;

  const canFund = isOrganizer && actualStatus === 'active' && hackathon.funded_amount === 0;
  const canEnd = isOrganizer && actualStatus === 'active' && !hackathon.manually_ended;

  return (
    <div className="card mb-3">
      <div className="card-body">
        <h5 className="card-title">{hackathon.title}</h5>
        <p className="card-text">{hackathon.description}</p>
        <p className="card-text"><strong>Start Date:</strong> {new Date(hackathon.start_date).toLocaleDateString()}</p>
        <p className="card-text"><strong>End Date:</strong> {new Date(hackathon.end_date).toLocaleDateString()}</p>
        <p className="card-text"><strong>Prize Pool:</strong> {hackathon.prize_pool} ETH</p>
        <p className="card-text"><strong>Participants:</strong> {hackathon.participant_count || 0}</p>
        <p className="card-text"><strong>Submissions:</strong> {hackathon.submission_count || 0}</p>
        <p className="card-text">
          <strong>Status:</strong>{' '}
          <span className={`badge ms-2 ${
            actualStatus === 'active' 
              ? 'bg-success' 
              : actualStatus === 'ended' 
              ? 'bg-danger' 
              : 'bg-secondary'
          }`}>
            {hackathon.manually_ended ? 'ENDED' : actualStatus ? actualStatus.toUpperCase() : 'UNKNOWN'}
          </span>
        </p>
        {hackathon.funded_amount > 0 && (
          <p className="card-text"><strong>Funded:</strong> {hackathon.funded_amount} ETH on {new Date(hackathon.funded_at).toLocaleString()}</p>
        )}
        <Link to={`/hackathons/${hackathon.id}`} className="btn btn-primary me-2">View Details</Link>
        {isOrganizer && (
          <>
            {canFund && (
              <button className="btn btn-success me-2" onClick={() => onFund(hackathon.id)}>
                Fund Hackathon
              </button>
            )}
            {canEnd && (
              <button 
                className="btn btn-danger" 
                onClick={() => {
                  console.log('End button clicked for hackathon:', hackathon.id);
                  onEnd(hackathon.id);
                }}
              >
                End Hackathon
              </button>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default HackathonCard;
