import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const LandingPage = () => {
  const user = JSON.parse(localStorage.getItem('user'));

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-gray-900 to-black">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="text-center p-6"
      >
        <div className="flex justify-center mb-4">
          <span className="icon-circle text-white text-2xl">üîç</span>
        </div>
        <h1 className="text-5xl font-bold bg-gradient-to-r from-indigo-400 to-purple-400 bg-clip-text text-transparent mb-4">
          Welcome to blockHUNT
        </h1>
        <p className="text-xl text-gray-300 mb-8">
          Join or organize hackathons on the blockchain!
        </p>
        {user ? (
          <p className="text-gray-300">
            Welcome, {user.email}! Check out the{' '}
            <Link to="/hackathons" className="btn-gradient inline-block hover:shadow-lg hover:scale-105 transition-all">
              Hackathons
            </Link>
          </p>
        ) : (
          <div className="space-x-4">
            <Link to="/signup" className="btn-gradient hover:shadow-lg hover:scale-105 transition-all">Sign Up</Link>
            <Link to="/login" className="btn-gradient bg-gray-700 hover:bg-gray-600 hover:shadow-lg hover:scale-105 transition-all">Login</Link>
          </div>
        )}
      </motion.div>
    </div>
  );
};

export default LandingPage;
