import React, { useState, useEffect } from 'react';
import HackathonCard from '../components/HackathonCard';
import axios from 'axios';
import { toast } from 'react-toastify';

const HackathonsPage = () => {
  const [hackathons, setHackathons] = useState([]);
  const [loading, setLoading] = useState(false);
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchHackathons = async () => {
      setLoading(true);
      try {
        const response = await axios.get('http://localhost:3000/api/hackathons');
        setHackathons(response.data);
      } catch (err) {
        console.error('Fetch hackathons error:', err);
        toast.error('Failed to load hackathons');
      } finally {
        setLoading(false);
      }
    };
    fetchHackathons();
  }, []);

  // Make sure any modifications to hackathons maintain the sorting
  const updateHackathon = (id, updates) => {
    const updatedHackathons = hackathons.map(h => 
      h.id === id ? { ...h, ...updates } : h
    );
    setHackathons(updatedHackathons);
  };

  // Separate active and non-active hackathons
  const activeHackathons = hackathons.filter(h => h.status === 'active');
  const endedHackathons = hackathons.filter(h => h.status !== 'active');

  if (loading) {
    return (
      <div className="text-center mt-6">
        <div className="spinner-border" role="status">
          <span className="visually-hidden"></span>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-6">
      <h2 className="text-2xl font-semibold mb-4">Hackathons</h2>
      {hackathons.length === 0 ? (
        <p>No hackathons available.</p>
      ) : (
        <>
          {activeHackathons.length > 0 && (
            <>
              <h3 className="text-xl font-medium mb-3">Active Hackathons</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
                {activeHackathons.map((hackathon) => (
                  <div key={hackathon.id} className="h-full">
                    <HackathonCard
                      hackathon={hackathon}
                      user={user}
                      isOrganizer={user?.role === 'organizer' && user?.id === hackathon.organizer_id}
                      onFund={(id) => {
                        updateHackathon(id, {
                          funded_amount: hackathon.prize_pool,
                          funded_at: new Date().toISOString()
                        });
                      }}
                      onEnd={(id) => {
                        updateHackathon(id, {
                          status: 'ended',
                          manually_ended: true,
                          manually_ended_at: new Date().toISOString()
                        });
                      }}
                    />
                  </div>
                ))}
              </div>
            </>
          )}

          {endedHackathons.length > 0 && (
            <>
              <h3 className="text-xl font-medium mb-3">Ended Hackathons</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                {endedHackathons.map((hackathon) => (
                  <div key={hackathon.id} className="h-full">
                    <HackathonCard
                      hackathon={hackathon}
                      user={user}
                      isOrganizer={user?.role === 'organizer' && user?.id === hackathon.organizer_id}
                      onFund={(id) => {
                        updateHackathon(id, {
                          funded_amount: hackathon.prize_pool,
                          funded_at: new Date().toISOString()
                        });
                      }}
                      onEnd={(id) => {
                        updateHackathon(id, {
                          status: 'ended',
                          manually_ended: true,
                          manually_ended_at: new Date().toISOString()
                        });
                      }}
                    />
                  </div>
                ))}
              </div>
            </>
          )}
        </>
      )}
    </div>
  );
};

export default HackathonsPage;
